  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const logoutHandler = () => {
    setIsLoggedIn(false);
  };

  const loginHandler = () => {
    setIsLoggedIn(true);
  };

  return <AuthContext.Provider>{props.children}</AuthContext.Provider>;
};

export default AuthContext;
